$(document).ready(function() {
  var totalQuestions = $('.question').length;
  var progressWidth = 0;
  var charactersThreshold = 3;

 
  $('.question input[type="text"]').on('input', function() {
      console.log("Text input changed");
      if ($(this).val().length >= charactersThreshold) {
          console.log("Text input meets character threshold");
          updateProgress();
      }
  });

  
  $('.question select').change(function() {
      console.log("Select input changed");
      updateProgress();
  });

  
  $('input[type="radio"]').click(function() {
      console.log("Radio button clicked");
      updateProgress();
  });

  
  function updateProgress() {
      var filledInputs = $('.question input[type="text"]').filter(function() {
          return $(this).val().length >= charactersThreshold;
      }).length + $('.question select').filter(function() {
          return $(this).val() !== "";
      }).length + $('input[type="radio"]:checked').length;

      progressWidth = (filledInputs / totalQuestions) * 100;
      console.log("Progress width:", progressWidth);
      $('#progress').css('width', progressWidth + '%');
  }
});
$(document).ready(function() {
  var totalQuestions = $('.question').length;
  var charactersThreshold = 3;

  
  $(document).on('input', '.question input[type="text"]', function() {
      updateProgress();
  });

  
  $(document).on('change', '.question select', function() {
      updateProgress();
  });

 
  $(document).on('click', 'input[type="radio"]', function() {
      updateProgress();
  });

 
  function updateProgress() {
      var filledInputs = $('.question input[type="text"]').filter(function() {
          return $(this).val().length >= charactersThreshold;
      }).length + $('.question select').filter(function() {
          return $(this).val() !== "";
      }).length + $('input[type="radio"]:checked').length;

      var progressWidth = (filledInputs / totalQuestions) * 100;
      $('#progress').css('width', progressWidth + '%');

      
      if (progressWidth >= 100) {
          $('.question-box').hide();
          $('#thank-you').removeClass('hidden');
      }
  }
});
